package chapitre1;

public class ExempleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//On veut afficher les 5 premiers nombres 1, 2, 3 ,4 et 5
		int i ;
		for (i=1; i<=5 ; i++) {
			System.out.print(i+"  ");
		}
		System.out.println("\nFin de la boucle, i="+i);
	}
}
